INSERT INTO Medicamento(Nome, GenericoNome, FarmaciaPopularNome,ComercialNome, HTML, Search) 
VALUES('Diclofenaco resinato','Diclofenaco resinato.',NULL,'Biofenac®.','<p class="corpo-do-texto"><span class="arial-bold-vermelho">Grupo farmacológico.</span> Analgésico; anti-inflamatório não esteroide; inibidor da COX-1 e COX-2.</p>
<p class="corpo-do-texto"><span class="arial-bold-vermelho">Apresentação/preço.</span> Fr-gts de 20 mL com 15 mg/mL (<span class="cifr-o-arial-bold-vermelho">R$</span> 7,16). </p>
<p class="corpo-do-texto"><span class="arial-bold-vermelho">Apresentação/preço.</span> Fr-gts de 20 mL com 15 mg/mL (<span class="cifr-o-arial-bold-vermelho">R$</span> 9,52).</p>
